mapper.select:
/*::::selectCourseList::::*/ select a.seq , a.id as course_id, a.course_code , a.target_types , a.teach_id, a.name , a.main_yn , a.main_display , a.icon_displays , a.complete_time , a.learn_types , a.credit , a.learn_target , a.price , a.prog_check , a.open_date , a.app_date , a.recruit , a.exam_period , a.main_tb_id , a.sub_tb_id , a.main_price , a.sub_price , a.link_server , a.group_yn , a.mobile_yn , a.index_url , a.tasting_url , a.sample_url , a.selp_period , a.dup_dis_yn , a.accept_yn , a.purpose , a.summary , a.instructor , DATE_FORMAT(a.reg_date, "%Y.%c.%e %T") as reg_date, DATE_FORMAT(a.upd_date, "%Y.%c.%e %T") as reg_date, a.credit*15 as credit_time, a.applyform_yn, ( SELECT name FROM FF_MEMBER WHERE ID = a.teach_id AND use_yn = 'Y' ) AS teacher_name, ( SELECT NAME FROM CB_BOOK WHERE ID = a.main_tb_id ) AS main_tb_name, ( SELECT URL_PATH FROM FF_ATTACH_FILE_DETAIL WHERE FILE_ID = a.MAIN_IMG_FILE LIMIT 1 ) AS photo_url from CB_COURSE a where 1=1 and a.use_yn = 'Y' and a.course_code like '%CATESEQ%'
and a.course_code in (select code from FF_CODE where use_yn = 'y' and parent_code in (select code from FF_CODE where use_yn = 'y' and parent_code = (select code from FF_CODE where parent_code is null and name=#{grand}) ) )
ORDER BY a.reg_date DESC
ORDER BY a.name ASC
limit ${startPage}, ${visiblePages}

SELECT a.seq, a.id, a.course_code, a.com_id, a.com_cal, a.teach_id, a.teach_cal, a.tutor_cal, a.tb_code, a.name, a.short_name, a.main_yn, a.main_display, a.icon_displays, a.complete_time, a.learn_types, a.target_types, a.credit, a.learn_target, a.price, a.prog_check, a.open_date, a.app_date, a.recruit, a.exam_period, a.main_tb_id, a.sub_tb_id, a.main_price, a.sub_price, a.link_server, a.group_yn, a.mobile_yn, a.index_url, a.tasting_url, a.sample_url, a.width, a.height, a.selp_period, a.main_img_file, a.dup_dis_yn, a.accept_yn, a.purpose, a.summary, a.instructor, a.note, a.mobile_summary, a.developer, a.res_tel, a.com_name, a.com_tel, a.produce_date, a.storage_loc, a.history, a.remarks, a.order_num, a.sell_order, a.hits_order, a.use_yn, a.reg_user, a.reg_date, a.upd_user, a.upd_date, a.page_codes, ( SELECT URL_PATH FROM FF_ATTACH_FILE_DETAIL WHERE FILE_ID = a.MAIN_IMG_FILE LIMIT 1 ) AS photo_url, a.comp_percent_quiz, a.comp_percent_exam, a.comp_percent_prog, a.comp_score_quiz, a.comp_score_exam, a.comp_score_prog, a.complete_total, a.applyform_yn
FROM CB_COURSE a
WHERE id = #{course_id}

