select a.seq , a.id as course_id, a.course_code , a.target_types , a.teach_id, a.name , a.main_yn , a.main_display , a.icon_displays , a.complete_time , a.learn_types , a.credit , a.learn_target , a.price , a.prog_check , a.open_date , a.app_date , a.recruit , a.exam_period , a.main_tb_id , a.sub_tb_id , a.main_price , a.sub_price , a.link_server , a.group_yn , a.mobile_yn , a.index_url , a.tasting_url , a.sample_url , a.selp_period , a.dup_dis_yn , a.accept_yn , a.purpose , a.summary , a.instructor , DATE_FORMAT(a.reg_date, "%Y.%c.%e %T") as reg_date, DATE_FORMAT(a.upd_date, "%Y.%c.%e %T") as reg_date, a.credit*15 as credit_time, ( SELECT name FROM FF_MEMBER WHERE ID = a.teach_id AND use_yn = 'Y' ) AS teacher_name, ( SELECT NAME FROM CB_BOOK WHERE ID = a.main_tb_id ) AS main_tb_name, ( SELECT URL_PATH FROM FF_ATTACH_FILE_DETAIL WHERE FILE_ID = a.MAIN_IMG_FILE LIMIT 1 ) AS photo_url, ( SELECT NAME FROM FF_CODE WHERE CODE = ( SELECT PARENT_CODE FROM FF_CODE WHERE CODE = ( SELECT PARENT_CODE FROM FF_CODE WHERE CODE = a.COURSE_CODE ) )
and name not in('Biz Expert(지역전문가)','글로벌 S/W Master', '') ) AS grand_name from CB_COURSE a where a.name LIKE CONCAT('%', #{keyWord} ,'%') and a.CHECKONLINE = 'online' and a.use_yn = 'Y'
order by reg_date desc limit ${startPage}, ${visiblePages}

