mapper.select:
SELECT CTP.PAY_TOEFL_SEQ, /*sequence*/ CTP.pay_user_method as PAYMENT_TYPE, CTP.pay_user_status as PAYMENT_STATUS, CTP.PAY_ACCNUM as ACCNUM, CTP.PAY_BANKNAME as BANKNAME, CTP.REAL_PAY_AMOUNT, CTP.DIS_POINT AS DIS_POINT, /*마일리지 사용금액*/ date_format(CTP.PAY_UDT_DT, '%Y-%m-%d') as PAY_UDT_DT, CC1.TOEFL_TITLE, CC1.TOEFL_PRICE, CC1.TOEFL_ID FROM CB_TOEFL_PAY_TB AS CTP LEFT OUTER JOIN CB_TOEFL CC1 ON CTP.TOEFL_ID = CC1.TOEFL_ID WHERE CTP.PAY_USER_ID = #{user_id}
AND CC1.TOEFL_TITLE LIKE CONCAT('%', #{search} ,'%')
ORDER BY CTP.PAY_UDT_DT DESC limit ${startPage}, ${visiblePages}

mapper.update:
